import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      datas: []
    }

    this.submitValues = this.submitValues.bind(this);
    this.fetchDataFromServer = this.fetchDataFromServer.bind(this);
  }

  componentDidMount() {
      this.fetchDataFromServer();
  }

  fetchDataFromServer = () => {
      fetch('http://localhost:3000/datas')
      .then((response) => {
        return response.json();
      })
      .then((myJs) => {
        this.setState({datas: myJs});
      });
  }
  nameValues = (e) => {
    this.setState({name: e.target.value})
    //console.log(this.state.name);
  }

  submitValues = () => {
    //alert("submited " + this.state.name);
    let obj = {name: this.state.name}
    fetch('http://localhost:3000/datas', {
                method: 'POST',
                headers : {"Content-Type": "application/json"},
                body:JSON.stringify(obj)
            }).then((res) => res.json())
            .then((data) =>  {
              this.fetchDataFromServer()
              console.log(data);
            })
            .catch((err)=>console.log(err))
  }

  deleteValue = (index) => {
    alert("deleted " + index);
  }


  render() {
    const {datas} = this.state;
    return (
      <div className="App">
        <p>Salai Deva</p>
        {datas.map((item, index) => (
          <table>
            <tr key={index}>
              <td>{item.name}</td>
              <td>
                <button onClick = {() => this.deleteValue(index)}>Delete</button>
              </td>
            </tr>
          </table>
        ))}

        <div>
          <p>Add Name</p>
          <p>Name: <input type="text" value={this.state.name} onChange={this.nameValues}/></p>
          <button onClick={this.submitValues}>Submit</button>
        </div>
      </div>
    );
  }
}

export default App;
