import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      datas: []
    }

    this.submitValues = this.submitValues.bind(this);
  }


  componentDidMount() {
    fetch('http://localhost:3000/datas')
    .then((response) => {
      return response.json();
    })
    .then((myJs) => {
      const values = JSON.parse(JSON.stringify(myJs));
      this.setState({datas: values});
      console.log(this.state.datas);
    })
  }
  nameValues = (e) => {
    this.setState({name: e.target.value})
    console.log(this.state.name);
  }

  submitValues = () => {
    alert("submited " + this.state.name);
    fetch('http://localhost:3000/datas', {
                method: 'POST',
                headers : new Headers(),
                body:JSON.stringify({name: this.state.name})
            }).then((res) => res.json())
            .then((data) =>  console.log(data))
            .catch((err)=>console.log(err))
  }


  render() {
    const datas1 = this.state.datas;
    return (
      <div className="App">
        <p>Salai Deva</p>
        {datas1.map((item, index) => (
          <ul key = {index}>
            <li>{item.name}</li>
          </ul>
        ))}

        <div>
          <p>Add Name</p>
          <p>Name: <input type="text" value={this.state.name} onChange={this.nameValues}/></p>
          <button onClick={this.submitValues}>Submit</button>
        </div>
      </div>
    );
  }
}

export default App;
