import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      datas: []
    }

    this.submitValues = this.submitValues.bind(this);
  }


  componentDidMount() {
    fetch('https://gist.githubusercontent.com/Salaidevaganamani/506b059aa029b018e04a8e88f2cfce83/raw/5a518ac78070d805a63061767bafc858965b71fe/dummy.json')
    .then((response) => {
      return response.json();
    })
    .then((myJs) => {
      const values = JSON.parse(JSON.stringify(myJs));
      this.setState({datas: values});
      console.log(this.state.datas);
    })
  }
  nameValues = (e) => {
    this.setState({name: e.target.value})
    console.log(this.state.name);
  }

  submitValues = () => {
    alert("submited " + this.state.name);
    //const proxyurl = "https://cors-anywhere.herokuapp.com/";
    var url = 'https://gist.githubusercontent.com/Salaidevaganamani/506b059aa029b018e04a8e88f2cfce83/raw/5a518ac78070d805a63061767bafc858965b71fe/dummy.json';
    var data = {"name": this.state.name};

    fetch(url, {
      method: 'PUT', // or 'PUT'
      body: JSON.stringify(data), // data can be `string` or {object}!
      credentials: 'omit',
      headers:{
        "Access-Control-Allow-Origin":"*",
        'Content-Type': 'application/json'
      }
    }).then(res => res.json())
    .then(response => console.log('Success:', JSON.stringify(response)))
    .catch(error => console.error('Error:', error));
  }


  render() {
    const datas1 = this.state.datas;
    return (
      <div className="App">
        <p>Salai Deva</p>
        {datas1.map((item, index) => (
          <ul key = {index}>
            <li>{item.name}</li>
          </ul>
        ))}

        <div>
          <p>Add Name</p>
          <p>Name: <input type="text" value={this.state.name} onChange={this.nameValues}/></p>
          <button onClick={this.submitValues}>Submit</button>
        </div>
      </div>
    );
  }
}

export default App;
