import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      datas: []
    }

    this.submitValues = this.submitValues.bind(this);
    this.fetchDataFromServer = this.fetchDataFromServer.bind(this);
  }

  componentDidMount() {
      this.fetchDataFromServer();
  }

  fetchDataFromServer = () => {
      fetch('http://localhost:3000/datas')
      .then((response) => {
        return response.json();
      })
      .then((myJs) => {
        this.setState({datas: myJs});
      });
  }
  nameValues = (e) => {
    this.setState({name: e.target.value})
    //console.log(this.state.name);
  }

  submitValues = () => {
    //alert("submited " + this.state.name);
    let obj = {name: this.state.name}
    fetch('http://localhost:3000/datas', {
                method: 'POST',
                headers : {"Content-Type": "application/json"},
                body:JSON.stringify(obj)
            }).then((res) => res.json())
            .then((data) =>  {
              this.fetchDataFromServer()
              console.log(data);
            })
            .catch((err)=>console.log(err))
  }

  deleteValue = (index) => {
    alert("deleted " + index);
    fetch('http://localhost:3000/datas/' + index, {
                method: 'DELETE',
                headers : {"Content-Type": "application/json"}
            }).then((res) => res.json())
            .then((data) =>  {
              this.fetchDataFromServer()
            })
            .catch((err)=>console.log(err))
  }


  render() {
    const {datas} = this.state;
    return (
      <div className="App">
        <p>Employee Names</p>
        <table>
          <thead>
            <tr>
              <th>Emp ID</th>
              <th>Name</th>
              <th>Option</th>
            </tr>
          </thead>
        {datas.map((item, index) => (
          <tbody key={index}>
            <tr>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>
                <button onClick = {() => this.deleteValue(item.id)}>Delete</button>
              </td>
            </tr>
          </tbody>
        ))}
        </table>
        <div style={{display:none}}>
          <p>Add Name</p>
          <p>Name: <input type="text" disabled={this.state.submitDisable} value={this.state.name} onChange={this.nameValues}/></p>
          <button onClick={this.submitValues} disabled={this.state.submitDisable}>Submit</button>
        </div>
      </div>
    );
  }
}

export default App;
